package com.example.babul.wsmt.models;import android.os.Parcel;import android.os.Parcelable;import com.google.gson.annotations.Expose;import com.google.gson.annotations.SerializedName;import java.util.List;/** * Created by user on 06-Feb-18. */public class ProfileBase implements Parcelable {    @SerializedName("return")    private boolean returnValue;    @SerializedName("data")    private List<Profile> datalist;    private ProfileBase(Parcel in) {        returnValue = in.readByte() != 0;        datalist = in.createTypedArrayList(Profile.CREATOR);    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeByte((byte) (returnValue ? 1 : 0));        dest.writeTypedList(datalist);    }    @Override    public int describeContents() {        return 0;    }    public static final Creator<ProfileBase> CREATOR = new Creator<ProfileBase>() {        @Override        public ProfileBase createFromParcel(Parcel in) {            return new ProfileBase(in);        }        @Override        public ProfileBase[] newArray(int size) {            return new ProfileBase[size];        }    };    public boolean isReturnValue() {        return returnValue;    }    public void setReturnValue(boolean returnValue) {        this.returnValue = returnValue;    }    public List<Profile> getDatalist() {        return datalist;    }    public void setDatalist(List<Profile> datalist) {        this.datalist = datalist;    }}